import React, { useState } from 'react';
import Sidebar from "./Sidebar";  
import {faHome , faList , faCog , faAbaout} from '@fortawesome/fontawesome-free-solid';
export default function Navbar() {
  const [showSidebar, setShowSidebar] = useState(true);
  const links = [
    {name: "Home", path: "/",icon: faHome},
    {name: "About", path: "/about",icon: faList},
    {name: "Recipes", path: "/pecipes"},
    {name: "Preferences", path: "/preferences"}

];
  return (
    <>
        <div className="navbar container">
            <a href="#!" className="logo"><span>Recipe</span>Finder</a>
            <div className="nav-links">
                {links.map(link => (
                    <a href="#!" key={link.name}>{link.name}</a>
                ))}
            {/* <a href="#">Home</a>
            <a href="#">About</a>
            <a href="#">Recipes</a>
            <a href="#">Preferences</a> */}
            </div>
            <div onClick={() => setShowSidebar(!showSidebar)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
            <div className="bar"></div>
            <div className="bar"></div>
            <div className="bar"></div>
            </div>
        </div>
         {showSidebar && <Sidebar links={links} />}
    </>
  );
}
