import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faList, faCog, faInfoCircle } from '@fortawesome/free-solid-svg-icons';  // Ensure you have the correct icons
import Sidebar from "./Sidebar";  

export default function Navbar() {
  const [showSidebar, setShowSidebar] = useState(true);
  const links = [
    { name: "Home", path: "/", icon: faHome },
    { name: "About", path: "/about", icon: faInfoCircle },
    { name: "Recipes", path: "/recipes", icon: faList },
    { name: "Preferences", path: "/preferences", icon: faCog }
  ];

  return (
    <>
      <div className="navbar container">
        <a href="#!" className="logo"><span>Recipe</span>Finder</a>
        <div className="nav-links">
          {links.map(link => (
            <NavLink to={link.path} key={link.name} activeClassName="active">
              <FontAwesomeIcon icon={link.icon} /> {link.name}
            </NavLink>
          ))}
        </div>
        <div onClick={() => setShowSidebar(!showSidebar)} className={showSidebar ? "sidebar-btn active" : "sidebar-btn"}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
      </div>
      {showSidebar && <Sidebar links={links} />}
    </>
  );
}
